CKEDITOR.editorConfig = function( config ) {
    config.language='fa';
    config.removePlugins='about,a11yhelp,find,flash,forms,iframe,magicline,pagebreak,pastefromword,preview,scayt,showblocks,smiley,specialchar,templates,wsc';
    config.height='360px';
    config.width='100%';
    config.removeButtons = 'Image,Link';
};
